import React, {Component} from 'react';
import TodoDataService from '../../api/todo/TodoDataService';
import AuthenticationService from './AuthenticationService.js'

// Display Todos
class ListTodosComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            todos:
                [
                    // hardcoded elements
                    // { id: 1, description: "learn react", done: false, targetDate: new Date() },
                    // { id: 2, description: "learn spring", done: false, targetDate: new Date() },
                    // { id: 3, description: "learn git", done: false, targetDate: new Date() }
                ]
        }
        this.deleteTodoClicked = this.deleteTodoClicked.bind
    }


    // bring todos from axios get into current state for display, after initial render has been triggered.
    componentDidMount() {
        let username = AuthenticationService.getLoggedInUserName
        TodoDataService.retrieveAllTodos(username)
        .then (
            response => {
                this.setState({todos : response.data})
                // console.log(response)
            }
        )
    }

    deleteTodoClicked(id) {
        let username = AuthenticationService.getLoggedInUserName();
        console.log(id + " " + username);
    }

    render() {
        return (
            <div>
                <h1>List of Todos</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Is Completed</th>
                                <th>Target Date</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                // script that for each todo, map it and display id and desc
                                this.state.todos.map(
                                    todo =>
                                        <tr key = {todo.id}>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                            <td><button className = "btn btn-warning">Delete</button></td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListTodosComponent;
