import React, { Component } from 'react'
import PropTypes from 'prop-types'
import './Counter.css'


class Counter extends Component {

    constructor() {
        super(); // Call super before this. No implicit super call in JS.
        this.state = {
            counter: 0
        }

         // bind increment function to the object
         this.increment = this.increment.bind(this);
    }


    render() {
        return (
          <div className="App">
            <CounterButton by = {1}/>
            <CounterButton by = {5}/>
            <CounterButton by = {10}/>
          </div>
        );
      }


      // Function increment
    // Update state => Counter ++

    // increment = () => {  using arrow function removes need to do binding. But we will stick to using binding for now

    increment() {
        this.setState({ // merge current state with new state
            counter: this.state.counter //this.props.by
        });
        // console.log('increment');
    }
}

// Class Counter with functions inside
class CounterButton extends Component {

    // Define initial state in a constructor
    // state => counter 0
    constructor() {
        super(); // Call super before this. No implicit super call in JS.
        this.state = {
            counter: 0
        }
        // bind increment function to the object
        this.increment = this.increment.bind(this);
    }

    // Function render
    render() {
        return (
            <div className="counter">
                <button onClick={this.increment}>+{this.props.by}</button>
                <span className="count">{this.state.counter}</span>
            </div>
        );
    }

    // Function increment
    // Update state => Counter ++

    // increment = () => {  using arrow function removes need to do binding. But we will stick to using binding for now

    increment() {
        this.setState({ // merge current state with new state
            counter: this.state.counter + this.props.by
        });
        // console.log('increment');
    }
}

CounterButton.defaultProps = { // default value for properties
    by : 1
}

CounterButton.propTypes = { // type constraints
    by : PropTypes.number
}


export default Counter ;
