import React, {Component} from 'react';
import AuthenticationService from './AuthenticationService.js';

// Log In
class LoginComponent extends Component {

    constructor(props) {
        super(props) // JS method call don't have ;
        this.state = { // initial state in JSON
            username: 'testUser',
            password: 'testPass',
            hasLoginFailed: false, // boolean 1
        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }


    // handle change in a field (manual way)
    handleChange(event) {
        this.setState(
            {
                // pull name from event and change that in the state to the new value from the event
                // square bracket to tell JS you are handling a variable instead of constant
                [event.target.name]: event.target.value
            }
        );
        console.log(this.state)
    }

    // On login click, update state variables. 
    // If successful, use authenticationService to register the log in ad redirect to the welcone page for that user
    // Else, set login failed to true to display error
    loginClicked() {
        if (this.state.username === 'testUser' && this.state.password === 'testPass') { // 3 ===
            AuthenticationService.registerSuccessfulLogin(this.state.username, this.state.password);
            this.props.history.push(`/welcome/${this.state.username}`);
        }
        else {
            this.setState({ hasLoginFailed: true }) // login has failed
        }
    }

    render() {
        return (
            // In JavaScript, BOOLEAN && SHOW X means IF BOOLEAN, THEN SHOW X
            //
            <div>
                <h1>Login</h1>
                <div className="container">
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                </div>
            </div>
        )
    }
}

export default LoginComponent;
