import React, { Component } from 'react'
// import routing features
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom'

class TodoApp extends Component {

    // root component of TodoApp
    // using react router dom dependency to route pages
    // default route (invalid page to ErrorComponent)
    // switch emsures only one of the path is launched (else pages will be merged)
    render() {
        return (
            <div className="TodoApp">
                <Router>
                    <HeaderComponent />
                    <Switch>
                        <Route path="/" exact component={LoginComponent} />
                        <Route path="/login" component={LoginComponent} />
                        <Route path="/logout" component={LogoutComponent} />
                        <Route path="/welcome/:name" component={WelcomeComponent} />
                        <Route path="/todos" component={ListTodosComponent} />
                        <Route component={ErrorComponent} />
                    </Switch>
                    <FooterComponent />
                </Router>
            </div>
        )
    }
}

// Log In
class LoginComponent extends Component {

    constructor(props) {
        super(props) // JS method call don't have ;
        this.state = { // initial state in JSON
            username: '',
            password: '',
            hasLoginFailed: false, // boolean 1
        }
        // handle events
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                // pull name from event and change that in the state to the new value from the event
                // square bracket to tell JS you are handling a variable instead of constant
                [event.target.name]: event.target.value
            }
        );
        console.log(this.state)
    }

    // on login click, update state variables
    loginClicked() {
        if (this.state.username === 'test' && this.state.password === 'test') { // 3 ===
            this.props.history.push(`/welcome/${this.state.username}`);
        }
        else {
            this.setState({ hasLoginFailed: true }) // login has failed
        }
    }

    render() {
        return (
            // Instead of using validation functions, do a simple JS boolean
            // In JavaScript, BOOLEAN && SHOW X means IF BOOLEAN, THEN SHOW X
            <div>
                <h1>Login</h1>
                <div className="container">
                    {/*<ShowInvalidCredentials hasLoginFailed = {this.state.hasLoginFailed}/>*/}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className = "btn btn-success"onClick={this.loginClicked}>Login</button>
                    {this.state.hasLoginFailed && <div className = "alert alert-warning">Invalid Credentials</div>}
                </div>
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="" className="navbar-brand">Home Link</a></div>
                    <ul className="navbar-nav">
                        <li><Link className="nav-link" to="/welcome/user">Home</Link></li>
                        <li><Link className="nav-link" to="/todos">Todos</Link></li>
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li><Link className="nav-link" to="/login">Login</Link></li>
                        <li><Link className="nav-link" to="/logout">Logout</Link></li>
                    </ul>
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <footer className="footer">
                <span className="text-muted">All Rights Reserved.</span>
            </footer>
        )
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <div>
                <h1>You are logged out</h1>
                <div className="container">
                    Thank you for using the application.
                </div>
            </div>
        );
    }
}

// Display Todos
class ListTodosComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            todos:
                [
                    { id: 1, description: "learn react", done: false, targetDate: new Date },
                    { id: 2, description: "learn spring", done: false, targetDate: new Date },
                    { id: 3, description: "learn git", done: false, targetDate: new Date }
                ]
        }
    }

    render() {
        return (
            <div>
                <h1>List of Todos</h1>
                <table>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>description</th>
                            <th>is Completed</th>
                            <th>target Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            // script that for each todo, map it and display id and desc
                            this.state.todos.map
                                (
                                    todo =>
                                        <tr>
                                            <td>{todo.id}</td>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                        </tr>
                                )
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}

// Welcome page
class WelcomeComponent extends Component {
    render() {
        return(
            <>
                <h1>Welcome</h1>
                <div className = "container">

                </div>
            </>
        )
        // Welcome, name fetched from component call inside router (:name)
        // No a href on link, this is single page app and we do not want to refresh
        // Use Link to
        return <div>Welcome {this.props.match.params.name}. You can manage your todos <Link to="/todos">here</Link></div>
    }
}

function ErrorComponent() {
    return <div>An error occured. Contact the site admin.</div>
}

// // tell user credentials are invalid
// function ShowInvalidCredentials(props) {
//     if(props.hasLoginFailed) {
//         return <div>Invalid Login</div>
//     }
//     return null
// }


export default TodoApp;
