import axios from 'axios'

class HelloWorldService {
    executeHelloWorldService() {
        return axios.get('http://localhost:8080/hello-world');
    }

    executeHelloWorldBeanService() {
        return axios.get('http://localhost:8080/hello-world-bean');
    }

    executeHelloWorldPathVariableService(name) {

        // REST 8080 username and password
        let username = 'user'
        let password = 'password'

        // Use username and password and encode into base64 to generate "basic" AuthHeader.
        let basicAuthHeader = 'Basic ' + window.btoa(username + ":" + password)

        return axios.get(
            x`http://localhost:8080/hello-world/path-variable/${name}`, 
        {
            headers : {
                // Authorization header for HTTP GET request
                authorization: basicAuthHeader
            }
        }
        );
    }
}

export default new HelloWorldService()
