import React, { Component } from 'react'

class TodoApp extends Component {
    render() {
        return (
            <div className="TodoApp">
                <LoginComponent></LoginComponent>
            </div>
        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props) // JS method call don't have ;
        this.state = { // initial state in JSON
            username: '',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                // pull name from event and change that in the state to the new value fro the event
                // square bracket to tell JS you are handling a variable instead of constant
                [event.target.name]: event.target.value
            }
        );
        console.log(this.state)
    }

    // on login click, update state variables
    loginClicked() {
        if (this.state.username === 'username' && this.state.password === 'password') { // 3 ===
            this.setState({ showSuccessMessage: true })
            this.setState({ hasLoginFailed: false })
        }
        else {
            this.setState({ hasLoginFailed: true })
            this.setState({ showSuccessMessage: false })
        }
    }

    render() {
        return (
            // each time it renders, launch two functions
            <div>
                <ShowInvalidCredentials hasLoginFailed = {this.state.hasLoginFailed}/>
                <ShowSuccessMessage showSuccessMessage = {this.state.showSuccessMessage}/>
                User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }
}

function ShowInvalidCredentials(props) {
    if(props.hasLoginFailed) {
        return <div>Invalid Login</div>
    }
    return null
}

function ShowSuccessMessage(props) {
    if(props.showSuccessMessage) {
        return <div>Login Successful</div>
    }
    return null
}

export default TodoApp;
