import axios from 'axios'

// Service for keeping track of currently authenticated user
// Also facilitates retrieval of currently logged in username
class AuthenticationService {

    executeBasicAuthenticationService(username, password) {
        return axios.get("http://localhost:8080/basicauth")
    }


    registerSuccessfulLogin(username, password) {

        // use username and password from 4200 as 8080 authentication
        let basicAuthHeader = 'Basic ' + window.btoa(username + ":" + password);

        sessionStorage.setItem('authenticatedUser', username);
        this.setupAxiosInterceptors(basicAuthHeader);
    }

    logout() {
        sessionStorage.removeItem('authenticatedUser');
    }

    // check if user is logged in
    isUserLoggedIn() {
        let user = sessionStorage.getItem('authenticatedUser');
        if (user === null) return false
        return true
    }

    // retrieve current user's username
    getLoggedInUserName() {
        let user = sessionStorage.getItem('authenticatedUser');
        if (user === null) return ''
        return user
    }

    setupAxiosInterceptors(basicAuthHeader) {
        axios.interceptors.request.use(
            (config) => {
                if (this.isUserLoggedIn()) {
                    config.headers.authorization = basicAuthHeader
                }
                return config;
            }
        )
    }
}

export default new AuthenticationService();
